import React, { HTMLAttributes } from 'react';
import { Elevation } from '../../interfaces';
export interface CardProps {
    children?: React.ReactNode | React.ReactNode[];
    /** The card description */
    subtitle?: string;
    /** Display the card heading */
    title?: string;
    /** The background color of the card */
    backgroundColor?: string;
    /** The card elevation determining the shadow */
    shadow?: Elevation;
}
declare const Card: ({ children, title, subtitle, backgroundColor, shadow, ...other }: CardProps & HTMLAttributes<HTMLDivElement>) => JSX.Element;
export default Card;
