import * as React from 'react';
import type { Theme } from '@theme';
export interface ThemeProps {
    children: React.ReactNode | React.ReactNode[];
    theme?: Theme;
}
/**
 * ! IMPORTANT: The ThemeProvider component is to be placed at the root of an app
 * where Devfolio Design System components are to be used.
 * ! IMPORTANT: The theme object shape should match the one used by the design system so
 * as to prevent unexpected styling.
 * @param {object} theme the theme to be applied to the components. If not supplied,
 * it will use the default Devfolio theme
 */
declare const ThemeProvider: ({ children, theme }: ThemeProps) => JSX.Element;
export default ThemeProvider;
